import React from "react";
import Carousel from 'react-bootstrap/Carousel';


class Slider extends React.Component{
  constructor(props) {
    super(props)

    this.state = {
      sliderData:[],
      newsArray: this.props.newsArray

    }

  }

  handleData=()=>{
    console.log(this.state.newsArray);
    let newArr=[]
    for (let i = 0; i < 10; i++) {
      newArr.push(this.state.newsArray)
    }
    this.setState({
      sliderData:newArr
    })
    console.log(newArr);
  }
  // this.setState({
  //   sliderData:newArr
  // })

  render(){
      return(
          <>
          {this.props.newsArray.forEach((item,idx)=>{
                return <p>{item.headline}</p>


          })}
          <Carousel>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="https://sbooks.net/wp-content/uploads/2021/10/old-book-flying-letters-magic-light-background-bookshelf-library-ancient-books-as-symbol-knowledge-history-218640948.jpg"
          alt="First slide"
        />
        <Carousel.Caption>
          <h3>First slide label</h3>
          <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
        </Carousel.Caption>
      </Carousel.Item>


    </Carousel>
          </>
      )
  }
}

export default Slider
